// ArrayString[] suit = { "Clubs", "Diamonds", "Hearts", "Spades" };String[] rank = { "2", "3", "4", "5", "6", "7", "8", "9", "10","Jack", "Queen", "King", "Ace" };int i = (int) (Math.random() * rank.length);int j = (int) (Math.random() * suit.length);System.out.println(rank[i] + " of " + suit[j]);String[] deck = new String[suit.length * rank.length];for (int i = 0; i < suit.length; i++)    for (int j = 0; j < rank.length; j++)        deck[rank.length*i + j] = rank[i] + " of " + suit[j];        rank.length        13        suit.length        4for (int i = 0; i < 13; i++)    for (int j = 0; j < 4; j++)        deck[4*i + j] = rank[i] + " of " + suit[j];System.out.println() deck[0]System.out.println(deck[0]);// ExchangeString t = deck[i];deck[i] = deck[j];deck[j] = t;// Shuffleint N = deck.length;for(inti=0;i<N; i++){    int r = i + (int) (Math.random() * (N-i));    String t = deck[i] ;    deck[i] = deck[r];    deck[r] = t;}